mapper.select:
SELECT A.RNUM, A.* FROM ( SELECT MEMBER_NO, USER_ID, USER_PASSWORD, USER_NAME, USER_NICK, USER_EMAIL, USER_ADDRESS, USER_REGIDATE, USER_STATUS, USER_CALL, USER_CERTKEY, USER_CERTSTATUS, USER_FLATFORM, ROWNUM AS RNUM FROM MEMBER
ROWNUM <= #{endrow}
ORDER BY USER_REGIDATE DESC ) A
WHERE A.RNUM >= #{startrow}

mapper.update:
UPDATE MEMBER
SET USER_STATUS = 0
WHERE MEMBER_NO = #{memberno}

mapper.select:
SELECT A.RNUM, A.* FROM ( SELECT QUESTION_NO, Q.MEMBER_NO, QUESTION_TITLE, QUESTION_CONTENT, QUESTION_DATE, ANSWER_CONTENT, ANSWER_STATUS, QUESTION_DELETE_STATUS, QUESTION_TYPE, M.USER_NAME, ROWNUM AS RNUM FROM CUSTOMER_QA_BOARD Q JOIN MEMBER M ON Q.MEMBER_NO = M.MEMBER_NO
ROWNUM <= #{endrow}
ORDER BY QUESTION_DATE DESC ) A
WHERE A.RNUM >= #{startrow}

mapper.update:
UPDATE CUSTOMER_QA_BOARD
SET QUESTION_DELETE_STATUS = 0
WHERE QUESTION_NO = #{questionno}

UPDATE CUSTOMER_QA_BOARD
SET ANSWER_CONTENT = #{content}
WHERE QUESTION_NO = #{questionno}

UPDATE CUSTOMER_QA_BOARD
SET QUESTION_DELETE_STATUS = 0
WHERE QUESTION_NO = #{string}

mapper.select:
SELECT A.RNUM, A.*
FROM (
      SELECT X.*, ROWNUM AS RNUM
      FROM (
            SELECT TO_CHAR(USER_REGIDATE, 'yyyy-MM-dd') AS join_date,  NVL(count(*), 0) AS join_count
            FROM MEMBER
            WHERE TO_CHAR(USER_REGIDATE, 'yyyy-MM') = #{tochar}
            GROUP BY TO_CHAR(USER_REGIDATE, 'yyyy-MM-dd')
           ) X
	  
      WHERE ROWNUM <= #{endrow}
	  
     ) A

WHERE A.RNUM >= #{startrow}

		SELECT A.*, A.RNUM
		FROM (
           	  SELECT X.*, ROWNUM AS RNUM
              FROM (
                    SELECT LIST_NO, LIST_ARTIST, LIST_TITLE,
                           LIST_PRODUCT, LIST_CATEGORY, LIST_CONTENT,
                           LIST_BASE_PRICE, LIST_DATE, BUY_COUNT,
                           ISORDERED, ORDERED_CONTENT, LIST_STATUS
                    FROM LIST_BOARD
                    WHERE TO_CHAR(LIST_DATE, 'yyyy-MM') = #{tochar}
                    ORDER BY BUY_COUNT DESC, LIST_NO
                   ) X
			  
              WHERE ROWNUM <= #{endrow}
			  
          ) A
		
		WHERE A.RNUM >= #{startrow}

SELECT A.RNUM, A.* FROM ( SELECT LIST_NO, LIST_ARTIST, LIST_TITLE, LIST_PRODUCT, LIST_CATEGORY, LIST_CONTENT, LIST_BASE_PRICE, LIST_DATE, BUY_COUNT, ISORDERED, ORDERED_CONTENT, LIST_STATUS, ROWNUM AS RNUM FROM LIST_BOARD
ROWNUM <= #{endrow}
) A
WHERE A.RNUM >= #{startrow}
