mapper.select:
SELECT A.RNUM, A.* FROM ( SELECT MEMBER_NO, USER_ID, USER_PASSWORD, USER_NAME, USER_NICK, USER_EMAIL, USER_ADDRESS, USER_REGIDATE, USER_STATUS, USER_CALL, USER_CERTKEY, USER_CERTSTATUS, USER_FLATFORM, ROWNUM AS RNUM FROM MEMBER
ROWNUM <= #{endrow}
) A
WHERE A.RNUM >= #{startrow}

mapper.update:
UPDATE MEMBER
SET USER_STATUS = 0
WHERE MEMBER_NO = #{memberno}

mapper.select:
SELECT A.RNUM, A.* FROM ( SELECT QUESTION_NO, Q.MEMBER_NO, QUESTION_TITLE, QUESTION_CONTENT, QUESTION_DATE, ANSWER_CONTENT, ANSWER_STATUS, QUESTION_DELETE_STATUS, QUESTION_TYPE, M.USER_NAME, ROWNUM AS RNUM FROM CUSTOMER_QA_BOARD Q JOIN MEMBER M ON Q.MEMBER_NO = M.MEMBER_NO
ROWNUM <= #{endrow}
) A
WHERE A.RNUM >= #{startrow}

mapper.update:
UPDATE CUSTOMER_QA_BOARD
SET QUESTION_DELETE_STATUS = 0
WHERE QUESTION_NO = #{questionno}
