mapper.select:
select list_no, list_artist, list_title, list_product, list_category, list_content, list_base_price, list_date, buy_count, isordered, ordered_content, list_status
from(
	select lbb.*, rownum as rnum
	from(
		select distinct lb.*
		from list_board lb, list_option_board lob, list_hash_tag lht 
		where lb.list_no = lob.list_no and lob.list_no = lht.list_no
			
				AND lb.list_artist like '%'||#{searchData}||'%'
			
			
				AND lb.list_title like '%'||#{searchData}||'%'
			
			
				AND lb.list_product like '%'||#{searchData}||'%'
			
			
				AND lb.list_category like '%'||#{list_category}||'%'
			
			
				AND lht.list_hash in 
				
					#{hash}
				
			
			
				AND lob.option_stock != 0
			
			
				AND lb.list_status = 1
			
			
				AND lb.isordered = 0
			
			
				AND lb.isordered = 1
			
			order by lb.list_no
		) lbb
		where rownum <= #{PageDTO.endrow}
	)
	where rnum >= #{PageDTO.startrow}

select list_image_loc 
from list_image_board
where list_no = #{list_no}

select list_hash
from(
	select lbb.*, rownum as rnum
	from(
		select distinct lht.list_hash
		from list_hash_tag lht
		) lbb
	where rownum <= #{hashPage}
	)

select list_hash
from(
	select lbb.*, rownum as rnum
	from(
		select distinct lht.list_hash
		from list_hash_tag lht
		where list_hash like '%'||#{searchData}||'%'
		) lbb
	where rownum <= 5
	)

mapper.insert:
insert into list_image_board(list_image_no, list_no, list_image_loc) select seq_list_image_no.nextval, A.* from(
select max(list_no) as list_no, #{image_loc} as list_image_loc from list_board
) A

insert into list_hash_tag(hash_no, list_no, list_hash) select seq_hash_no.nextval, A.* from(
select max(list_no) as list_no, #{hashv} as list_hash from list_board
) A

insert into list_option_board(option_no, list_no, option_name, option_stock, option_price) select seq_option_no.nextval, A.* from(
select max(list_no) as list_no, #{opt.option_name} as option_name, #{opt.option_stock} as option_stock, #{opt.option_price} as option_price from list_board
) A

insert into list_order_board(list_order_no, list_no, order_name, order_option) select seq_list_order_no.nextval, A.* from(
select max(list_no) as list_no, #{ord.order_name} as order_name, #{ord.order_option} as order_option from list_board
) A

mapper.select:
select list_no, list_title, list_product, list_base_price, ordered_content, isordered
from list_board
where list_no = #{no}

select list_order_no, order_name, order_option
from list_order_board
where list_no = #{no}
